select * from customer limit 15


--q1. what is the total revenue generated by male vs. femae customers
SELECT 
gender ,sum(purchase_amt) as total_revenue
from customer group by gender

--q2which customers used a discount but still spent more than the average purchase amount 
select customer_id, purchase_Amt from customer 
where discount_applied = 'Yes' and purchase_Amt >= 
(select avg(purchase_amt) from customer )

--q3what are the top 5 products with the highest avg review_rating
select item_purchased , round(avg(review_rating::numeric),2) from customer 
group by item_purchased 
order by avg(review_rating) desc limit 5

--q4compare the average purchase amount between standard
--and express shipping
select shipping_type, round(avg(purchase_amt::numeric),2) as average_purchase_amt 
from customer where shipping_type in ('Standard','Express')
group by shipping_type

--q5do subscribed customers spend more? compare average spend and total_revenue 
--between subscribers and non subscribers
select subscription_status, count(customer_id) as total_customers,
avg(purchase_amt) as avg_spend, 
sum(purchase_amt) as  total_revenue
from customer group by subscription_status 
order by total_revenue , avg_spend desc

--q6which 5 products have the highest percentage of purchases with discounts applied
select item_purchased, 100*sum(case when discount_applied = 'Yes' then 1 else 0 end )/count(*) 
as discount_rate from customer
group by item_purchased
order by discount_rate desc limit 5

--q7segment customers into new,returning,loyal based on their total
--number of previous purchases and show the count of each segment
with customer_type as
(select customer_id, previous_purchases,
case when previous_purchases = 1 then 'New'
when previous_purchases between 2 and 10 then 'Returning'
else 'Loyal' end as customer_segment
from customer)
select customer_segment,count(*) as no_of_customers
from customer_type
group by customer_segment

--q8what are the top 3 most purchases products within each category 
with item_count as 
(select category,item_purchased,count(customer_id) as total_orders,
row_number()over(partition by category order by count(customer_id)desc )as item_rank
from customer
group by category,item_purchased
)
select item_rank, category ,item_purchased ,total_orders 
from item_count where item_rank <= 3;

--q9are customers who are repeat buyers (more then 5 previous purchases)
--also likely to subscribe
select subscription_status,count(customer_id) as repeat_buyers
from customer 
where previous_purchases > 5 
group by subscription_status 

--q10what is the revenue contribution of each age group
select age_group, sum(purchase_amt) as total_revenue
from customer
group by age_group
order by total_revenue desc